export function validate(input) {
  let errors = {};
 if(!input.name){
   errors.name= 'Por favor introduzca su nombre'
 }
 if(!input.lastName){
  errors.lastName= 'Por favor introduzca su apellido'
}
  if (!input.email) {
    errors.email = 'Por favor introduzca su email';
  } else if (!/\S+@\S+\.\S+/.test(input.email)) {
    errors.email = 'El email es invalido';
  }
if(!input.password){
  errors.password = 'Por favor introduzca su contraseña';
} else if (!/([A-Za-z][A-Za-z0-9]*[0-9][A-Za-z0-9])/.test(input.password)) {
  errors.password = 'La contraseña debe contener una letra mayuscula y al menos dos numeros';

}else if(input.password !== input.confirmPassword){
  errors.password= "Las contraseñas no coinciden"

} else if(input.birthday===""){
  errors.birthday= "Por favor introduzca su fecha de nacimiento"

}else if(input.address===""){
  errors.address= "Por favor introduzca su dirección"

}else if(input.city===""){
  errors.city= "Por favor introduzca su localidad"

}else if(input.province===""){
  errors.province= "Por favor introduzca su provincia"

}else if(input.cp===""){
  errors.cp= "Por favor introduzca su código postal"

}else if(input.country===""){
  errors.country= "Por favor introduzca su país"

}
  return errors;
};

export default connect(mapStateToProps, mapDispatchToProps)(Register);
